mapper.select:
select 

from dept_manager
where emp_no = #{empNo,jdbcType=INTEGER}
  and dept_no = #{deptNo,jdbcType=CHAR}

mapper.delete:
delete from dept_manager
where emp_no = #{empNo,jdbcType=INTEGER}
  and dept_no = #{deptNo,jdbcType=CHAR}

mapper.insert:
insert into dept_manager (dept_no, from_date, to_date
  )
values (#{deptNo,jdbcType=CHAR}, #{fromDate,jdbcType=DATE}, #{toDate,jdbcType=DATE}
  )

mapper.update:
update dept_manager


    from_date = #{fromDate,jdbcType=DATE},


    to_date = #{toDate,jdbcType=DATE},


where emp_no = #{empNo,jdbcType=INTEGER}
  and dept_no = #{deptNo,jdbcType=CHAR}

update dept_manager
set from_date = #{fromDate,jdbcType=DATE},
  to_date = #{toDate,jdbcType=DATE}
where emp_no = #{empNo,jdbcType=INTEGER}
  and dept_no = #{deptNo,jdbcType=CHAR}
